import React,{useEffect} from "react";
import {getRedirectResult} from 'firebase/auth'
import {signInWthGooglePopup,createUserDocumentFromAuth,signInWithGoogleRedirect} from '../../utils/firebase/firebase.utils'

const SignIn=()=>{

    
    const logoGoogleUser=async()=>{
        const {user}=await signInWthGooglePopup()
        // console.log(response);
       const userDocRef = await createUserDocumentFromAuth(user)
    }
    // const logGoogleRedirectUser = async()=>{
    //     const {user} = await signInWithGoogleRedirect()
    //     console.log({user})
    //}
    return(
        <div>
            <h1>Sign In please</h1>
            <button onClick={ logoGoogleUser} className="btn btn-outline-primary">
                SignIn With Google Popup
            </button>
            <button onClick={ signInWithGoogleRedirect } className="btn btn-outline-default">
                SignIn With Google Redirect
            </button>
        </div>


    )
}
export default SignIn;
