         </Link>
         <div className="links-container">
            <Link className="nav-link" to='/shop'>SHOP</Link>
            {
              currentUser?(
                <span className="nav-link" onClick={signOutU}>SIGN OUT</span>)
              :(
                <Link className="nav-link" to='/auth'>SIGN IN</Link>
              )
                
            }
           
         </div>
        </div>
        <Outlet/>
      </Fragment>
    );
  };
  export default Navigation;
