//import logo from "./logo.svg";
import "./App.css";
import './categories.styles.scss';

function App() {
  const catagories = [
    {
      id: 1,
      title: "Hats",
      imageUrl:'https://i.ibb.co/cvpntL1/hats.png%27'
    },
    {
      id: 2,
      title: "Jacket",
      imageUrl:'https://i.ibb.co/px2tCc3/jackets.png%27'
    },
    {
      id: 3,
      title: "Shoes",
      imageUrl:'https://i.ibb.co/0jqHpnp/sneakers.png%27'
    },
    {
      id: 4,
      title: "Men",
      imageUrl:'https://i.ibb.co/R70vBrQ/mens.png%27'
    },
    {
      id: 5,
      title: "Women",
      imageUrl:'https://i.ibb.co/GCCdy8t/womens.png%27'
    }
  ];
  return (
    <div className="categories-container">
      {catagories.map(({id,title,imageUrl}) => {
        return (
        <div  key={id} className="catagories-container">
          <div className="background-image" style={{
            backgroundImage:`url(${imageUrl})`
          }}>

          </div>
          <img src={imageUrl} alt={title}></img>
          <div className="catgory-body-container">
            <h2>{title}</h2>
            <p>shop now</p>
          </div>
        </div>);
      })}
    </div>
  );
}

export default App;
